/*eslint-disable */
import React, { Component } from "react"
import { Link, withRouter } from "react-router-dom"
import { connect } from "react-redux"
import { Button, Grid, Header, Label, Sticky, Search } from "semantic-ui-react"
import { logoutUser, findDisplayUser } from "../actions"
import _ from "lodash"

class Nav extends Component {
  state = {
    searchInput: "",
    results: []
  }

  handleLogout = e => {
    localStorage.clear()
    this.props.logoutUser()
    this.props.history.push("/login")
  }

  handleChange = (e, value) => {
    this.setState({ searchInput: value })
  }

  filterSearchResults = () => {
    this.props.allUsers.filter(user => {

      return user.username.includes(this.state.searchInput)
    })
  }

  goToCurrentUserProfile = user_id => {
    this.props.history.push(`/profile/${user_id}`)
    this.props.findDisplayUser(this.props.allUsers, this.props.history)
  }

  render() {
    // TODO Make the NAVBAR conditionally render based on 'isCurrentUser' and 'isLoggedIn'
    console.log(this.state)
    return (
      // <Sticky>
      <div className="nav-bar-container">
        <Button as={Link} to="/feed" content="Feed" />
        {/* {this.props.user.isLoggedIn ? ( */}
        <Button
          as={Link}
          onClick={user_id => this.goToCurrentUserProfile(this.props.user.id)}
          content="Prof"
        />
        <Button as={Link} to="/newsong" content="Upload Song" />
        <Button as={Link} to="/editaccount" content="Edit Profile" />
        <Button as={Link} onClick={this.handleLogout} content="Logout" />
        <Search
          results={this.filterSearchResults}
          onSearchChange={_.debounce(
            (event, { value }) => this.handleChange(event, value),
            500
          )}
          showNoResults={false}
        />
        {/* ) : ( */}
        <Button as={Link} to="/login" content="Login" />
        {/* )} */}
      </div>
      // </Sticky>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    allUsers: state.allUsers
  }
}

export default connect(
  mapStateToProps,
  { logoutUser, findDisplayUser }
)(withRouter(Nav))
