/*eslint-disable */
import React from "react"
import "../App.css"
import LoginForm from "./LoginForm"
import NewUserForm from "./NewUserForm"
import UserProfile from "./UserProfile"
import FeedContainer from "./FeedContainer"
import { connect } from "react-redux"
import { Router, Route, withRouter, Switch, Redirect } from "react-router-dom"
import { currentUser, logoutUser, fetchAllUsers, fetchAllSongs } from "../actions"
import { reducer } from "../reducers"
import NewSongForm from "./NewSongForm"

class App extends React.Component {
  // ! FOR TESTING OUT REDUX
  // state = {
  //   allSongs: []
  // }
  componentDidMount() {
    this.props.fetchAllSongs()
    this.props.fetchAllUsers()
    this.props.currentUser(this.props.history)
  }

  // FETCHSONGS = () => {
  //   fetch('http://localhost:3000/songs')
  //   .then(resp => resp.json())
  //   .then(data => {
  //     if (data.error) {
  //       console.log(data.error)
  //     } else {
  //       this.setState({allSongs: data})
  //     }
  //   })
  //   .catch(err => console.log(err))
  // }

  handleChange(e) {
    const targetValue = e.target.value
    const targetName = e.target.name
    this.setState({ [targetName]: targetValue })
  }

  handleLogout = e => {
    localStorage.clear()
    this.props.logoutUser()
    this.props.history.push("/login")
  }

  render() {
    // console.log('daddadadad', this.props)
    console.log(this.state)
    return (
      <div className="app-container">
        {/* <Switch> */}
        <Route path="/login" render={() => <LoginForm />} />
        <Route path="/createaccount" render={() => <NewUserForm />} />
        <Route
          path="/profile"
          render={() => <UserProfile handleLogout={this.handleLogout} />}
        />
        <Route
          path="/feed"
          render={() => <FeedContainer allSongs={this.props.allSongs}/>}
        />
        <Route 
          path='/newsong'
          render={() => <NewSongForm />}
        />
        {/* </Switch> */}
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    allUsers: state.allUsers
  }
}

const mapDispatchToProps = dispatch => {
  return {
    currentUser: history => dispatch(currentUser(history)),
    logoutUser: () => dispatch(logoutUser()),
    fetchAllUsers: () => dispatch(fetchAllUsers()),
    fetchAllSongs: () => dispatch(fetchAllSongs())
  }
}
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(App))
