/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"
import { Button, Grid } from "semantic-ui-react"
import SongFeedComponent from "./SongFeedComponent"
import {
  findDisplayUser,
  followUser,
  currentUser,
  currentRelationship,
  setCurrentUser,
  deleteUser
} from "../actions"

class UserProfile extends Component {
  componentDidMount() {
    this.asyncUserProfileProps()
  }

  // handleFollowUser = (followed_id, follower_id) => {
  handleFollowUser = (followed, follower_id) => {
    // console.log("followed: ", followed.id)
    // console.log("follower: ", follower_id)
    this.props.followUser(followed, follower_id)
  }

  asyncUserProfileProps = async () => {
    await this.props.currentUser()
    await this.props.findDisplayUser(this.props.allUsers, this.props.history)
    const relationship = this.props.displayUser.passive_relationships.find(
      rel => {
        return rel.follower_id === this.props.user.id
      }
    )
    this.props.setCurrentUser(this.props.displayUser, this.props.user)
    this.props.currentRelationship(relationship)
  }

  handleUnfollowUser = () => {
    // TODO Send DELETE fetch to backend
    // TODO Need the relationshipID
  }

  render() {
    const { isCurrentUser } = this.props.user
    const { username, name, location, bio, id } = this.props.displayUser
    const filteredDisplayUserSongs = this.props.allSongs.filter(displaySong => {
      return displaySong.song.user_id === id
    })
    const mappedDisplayUserSongFeed = filteredDisplayUserSongs.map(
      displaySong => {
        return (
          <SongFeedComponent songData={displaySong} userClickDisabled={true} />
        )
      }
    )
    // TODO Move isCurrentUser to Store
    // console.log("current user? ", isCurrentUser)
    // console.log("following? ", isFollowing)
    return true ? (
      <div className={`user-profile-${id}`}>
        <Grid columns={2}>
          <Grid.Column>{mappedDisplayUserSongFeed}</Grid.Column>
          <Grid.Column>
            <h1>Username: {username}</h1>
            <h1>Name: {name}</h1>
            <h1>location: {location}</h1>
            <p>Bio: {bio}</p>
            {isCurrentUser ? (
              <Button
                onClick={user_id => this.props.deleteUser(this.props.user.id)}
              >
                Delete Profile
              </Button>
            ) : (
              <Button
                onClick={(followed, follower_id) =>
                  this.handleFollowUser(
                    this.props.displayUser,
                    this.props.user.id
                  )
                }
              >
                Follow Me!
              </Button>
            )}
          </Grid.Column>
        </Grid>
      </div>
    ) : (
      <div>No User Profile</div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    displayUser: state.displayUser,
    allSongs: state.allSongs,
    allUsers: state.allUsers,
    followeds: state.followeds,
    followers: state.followers
  }
}
export default connect(
  mapStateToProps,
  {
    findDisplayUser,
    followUser,
    currentUser,
    currentRelationship,
    setCurrentUser,
    deleteUser
  }
)(withRouter(UserProfile))
