/*eslint-disable */
import React from "react"
import "../App.css"
import LoginForm from "./LoginForm"
import NewUserForm from "./NewUserForm"
import UserProfile from "./UserProfile"
import FeedContainer from "./FeedContainer"
import { connect } from "react-redux"
import { Router, Route, withRouter, Switch, Redirect } from "react-router-dom"
import { currentUser, logoutUser, fetchAllUsers, fetchAllSongs } from "../actions"
import { reducer } from "../reducers"
import NewSongForm from "./NewSongForm"

class App extends React.Component {
  // ! FOR TESTING OUT REDUX
  // state = {
  //   allFollowers: [],
  //   allFolloweds: []
  // }
  componentDidMount() {
    this.props.fetchAllSongs()
    this.props.fetchAllUsers()
    this.props.currentUser()
    // this.props.fetchFollowing(this.props.user.id)
  }

  handleLogout = e => {
    localStorage.clear()
    this.props.logoutUser()
    this.props.history.push("/login")
  }

  setAllFollowings = () => {
    let followedsIDArray = []
    let followersIDArray = []
    const userFollowings = this.props.allUsers.find(user => user.id === this.props.user.id)
    if (userFollowings) {
      followersIDArray = userFollowings.active_relationships.map(rel => rel.follower_id)
      followedsIDArray = userFollowings.passive_relationships.map(rel => rel.followed_id)
    }
    debugger
  }

  render() {
    // console.log('app props: ', this.props)
    // console.log(this.state)
    this.setAllFollowings()
    return (
      <div className="app-container">
        {/* <Switch> */}
        <Route path="/login" render={() => <LoginForm />} />
        <Route path="/createaccount" render={() => <NewUserForm />} />
        <Route
          // TODO Need to go to /profile/${displayUserId} 
          path="/profile/:id"
          render={() => <UserProfile handleLogout={this.handleLogout} />}
        />
        <Route
          path="/feed"
          render={() => <FeedContainer allSongs={this.props.allSongs}/>}
        />
        <Route 
          path='/newsong'
          render={() => <NewSongForm currentUser={this.props.user} handleChange={this.handleChange}/>}
        />
        {/* </Switch> */}
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    allUsers: state.allUsers
  }
}

export default connect(
  mapStateToProps,
  // mapDispatchToProps
  {currentUser, logoutUser, fetchAllUsers, fetchAllSongs}
)(withRouter(App))
