import React, { Component } from "react"
import Waveform from "./Waveform"
import { withRouter } from "react-router-dom"
import {connect} from 'react-redux'

class SongShowPage extends Component {

  findSong = displaySongID => {
    if (this.props.allSongs.length > 0) {
      return this.props.allSongs.find(u => {
        return u.id === displaySongID
      })
    }
  }

  render() {
    // TODO need current song
    console.log(this.props)
    const displaySongID = Number(this.props.history.location.pathname.slice(9))
    // const { username, name, location, bio, id } = this.props.user
    // const { allSongs } = this.props
    // this.props.fetchFollowing(this.props.user.id)
    const displayUser = this.findSong(displaySongID)
    const { username, name, location, bio, id } = displayUser
    const isCurrentUser = id === this.props.user.id
    const isFollowing = this.props.user.id === this.props.followeds.find(f => f.id === id )
    console.log('current user? ', isCurrentUser)
    console.log('following? ', isFollowing)

    return (
      <div className={`song-show`}>
        <Waveform
          // song={song}
          // song_link={songData.song_link}
          song={null}
          song_link={null}
          waveHeight={100}
          responsive={true}
          splitChannels={false}
          mediaControls={true}
          maxCanvasWidth={500}
        />
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  null
  // { currentSong }
)(withRouter(SongShowPage))
