/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"
import { Button, Grid, Header } from "semantic-ui-react"

import Waveform from "./Waveform"

class SongFeedComponent extends Component {
  goToUserProfile = e => {
    const user_id = this.props.songData.song.user.id
    this.props.history.push(`/profile/${user_id}`)
  }

  goToSongPage = e => {
    // const song_id = this.props.songData.song.user.id
    // this.props.history.push(`/profile/${song_id}`)
  }

  render() {
    // ! Need current song with data
    const { songData } = this.props
    const {
      songData: { song }
    } = this.props
    const {
      songData: {
        song: { user }
      }
    } = this.props
    // console.log(song)
    // TODO ADD the SongTags to Component
    return (
      <div className={`song-comp-${song.id}`}>
        <Grid.Row columns={4}>
          <Grid.Column>
            {/* <div style={{width: '20'}}> */}
              <h4 onClick={this.goToSongPage} style={{width: '50%'}}>{song.title}</h4>
            {/* </div> */}
          </Grid.Column>
          {/* <Header to={`/profile/${song.id}`} as="h4">{song.title}</Header> */}
        </Grid.Row>
        <Grid.Row celled>
          <Grid.Column floated="left">
            <h4 onClick={this.goToUserProfile}>{user.username}</h4>
            {/* <h4>{user.username}</h4> */}
          </Grid.Column>
          {/* <Grid.Column>sup</Grid.Column> */}
          {/* <Grid.Column>2</Grid.Column> */}
        </Grid.Row>
        <Grid.Row>
          <Waveform
            song_link={songData.song_link}
            waveHeight={100}
            responsive={true}
            splitChannels={false}
            mediaControls={true}
            maxCanvasWidth={500}
          />
        </Grid.Row>
        <br />
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
    // allUsers: state.allUsers,
    // allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  // null,
  null
)(withRouter(SongFeedComponent))
