import React, { Component } from "react"
import { connect } from "react-redux"
import { Button, Form, Grid, Header, Message, Segment } from "semantic-ui-react"

class NewSongForm extends Component {
  constructor() {
    super()
    this.audioInput = React.createRef()
    this.state = {
      title: "",
      genre: "",
      description: "",
      user_id: this.props.currentUser.id
      // song_link: ''
    }
  }

  handleNewSongSubmit = e => {
    e.preventDefault()
    console.log(this.audioInput.current.files[0])
    const newAudio = this.audioInput.current.files[0]
    const stateWithAudio = {...this.state, song_link: newAudio}
    console.log(stateWithAudio)
    const fetchSongsUrl = "http://localhost:3000/songs"
    const reqObj = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(stateWithAudio)
    }
    fetch(fetchSongsUrl, reqObj)
    .then(res => res.json())
    .then(console.log)
  }

  handleNewSongChange = e => {
    this.setState({ [e.target.name]: e.target.value })
  }
  render() {
    // console.log(this.state)
    console.log(this.props)
    return (
      <div className="new-song-form">
        <Grid textAlign="center" verticalAlign="middle">
          <Grid.Column>
            <Form size="large" onSubmit={this.handleNewSongSubmit}>
              <Segment stacked>
                <Header as="h1">New Song</Header>
                <Form.Input
                  onChange={this.handleNewSongChange}
                  placeholder="Title"
                  type="text"
                  name="title"
                />
                <Form.Input
                  onChange={this.handleNewSongChange}
                  placeholder="Genre"
                  type="text"
                  name="genre"
                />
                <Form.Input
                  onChange={this.handleNewSongChange}
                  placeholder="Description"
                  type="text"
                  name="description"
                />
                <input type='file' ref={this.audioInput} />
                {/* <Form.Input
                  fluid
                  placeholder="Audio File"
                  type="file"
                  ref={this.audioInput}
                  name="song_link"
                /> */}

                <Button type="submit" primary fluid size="large">
                  Submit New Song
                </Button>
              </Segment>
            </Form>
          </Grid.Column>
        </Grid>
      </div>
    )
  }
}

export default connect(
  null,
  null
)(NewSongForm)
