/*eslint-disable */
import React, { Component } from "react"
import { Link, withRouter } from "react-router-dom"
import { connect } from "react-redux"
import { Button, Grid, Header, Label, Sticky } from "semantic-ui-react"
import { logoutUser } from "../actions"

class Nav extends Component {
  handleLogout = e => {
    localStorage.clear()
    this.props.logoutUser()
    this.props.history.push("/login")
  }

  render() {
    console.log(this.props)
    return (
      <Sticky>
        <div className="nav-bar-container">
            {/* <Button as={Link} to="/feed">
              Feed
            </Button> */}
          {/* {this.props.user.isLoggedIn ? ( */}
              <Button as={Link} onClick={this.handleLogout} value='Logout' />
              <Button as={Link} to={`/profile/${this.props.user.id}`} value="Prof" />
              <Button as={Link} to="/newsong" value="Upload Song" />
          ) : (
              <Button as={Link} to="/login">
                Login
              </Button>
          )}
        </div>
      </Sticky>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  }
}

export default connect(
  mapStateToProps,
  // null,
  { logoutUser }
)(withRouter(Nav))
