//! All Reducers here
import { combineReducers } from 'redux'

export default combineReducers({
  currentUser: currentUser,
  auth: auth
 })

function currentUser(state={}, action){
  switch (action.type) {
    case 'LOGIN_USER':
      return {...action.currentUser}
    case 'LOGOUT_USER':
      return {}
    default:
      return state
  }
}
