/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"
import { Button, Grid, Header } from "semantic-ui-react"

import Waveform from "./Waveform"

class SongFeedComponent extends Component {

  goToUserProfile = () => {
    debugger
    const user_id = this.props.songData.song.user.id
    this.props.history.push(`/profile/${user_id}`)
  }

  goToSongPage = () => {

  }

  render() {
    // ! Need current song with data
    const { songData } = this.props
    const {
      songData: { song }
    } = this.props
    const {
      songData: {
        song: { user }
      }
    } = this.props
    // console.log(this.props)
    return (
      <div className={`song-comp-${song.id}`}>
        <Grid.Row>
          {/* <Header to={`/profile/${song.id}`} as="h4">{song.title}</Header> */}
          <h4>{song.title}</h4>
        </Grid.Row>
        <Grid.Row>
          <h4 onClick={this.goToUserProfile}>{user.username}</h4>
        </Grid.Row>
        <Grid.Row>
          <Waveform
            song_link={songData.song_link}
            waveHeight={100}
            responsive={true}
            splitChannels={false}
            mediaControls={true}
            maxCanvasWidth={500}
          />
        </Grid.Row>
        <br />
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
    // allUsers: state.allUsers,
    // allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  // null,
  null
)(withRouter(SongFeedComponent))
