/*eslint-disable */
import React from "react"
import ReactDOM from "react-dom"
import WaveSurfer from "wavesurfer.js"
import { connect } from "react-redux"
import { withRouter } from "react-router-dom"

class Waveform extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      playing: false,
      pos: 0
    }
  }
  componentDidMount() {
    const { waveHeight, responsive, splitChannels } = this.props
    this.$el = ReactDOM.findDOMNode(this)
    this.$waveform = this.$el.querySelector(".wave")
    if (this.$waveform) {
      this.wavesurfer = WaveSurfer.create({
        container: this.$waveform,
        mediaType: "audio",
        waveColor: "red",
        progressColor: "blue",
        height: waveHeight,
        maxCanvasWidth: 100,
        partialRender: true,
        splitChannels: splitChannels,
        // mediaControls: true,
        responsive: responsive
      })
      // this.$waveform.style.backgroundColor = "black"
      this.wavesurfer.load(this.props.song_link)
    } else {
      console.log('waveform loading...')
    }
  }

  handleTogglePlay = () => {
    if (this.state.playing) {
      this.wavesurfer.pause()
      this.setState({ playing: !this.state.playing })
    } else {
      this.wavesurfer.play()
      this.setState({ playing: !this.state.playing })
    }
  }

  handleStop = () => {
    this.setState(prevState => {
      this.wavesurfer.stop()
      return { playing: false, pos: 0 }
    })
  }

  render() {
    // return this.props.src ? (
    return (
      <div className="waveform">
        <div className="wave"></div>
        <button name="play-pause" onClick={this.handleTogglePlay}>
          Play/Pause
        </button>
        <button name="stop" onClick={this.handleStop}>
          Stop
        </button>
      </div>
    ) 
    // : ( 
    //   <div className='waveform'>da feeed is loadin..................</div>
    // )
  }
}

export default connect(
  null,
  null
)(withRouter(Waveform))

