/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { withRouter } from "react-router-dom"
import SongFeedComponent from "./SongFeedComponent"
import { Grid, Button, Dropdown } from "semantic-ui-react"
import _ from "lodash"

class FeedContainer extends Component {
  state = {
    tagFilter: ""
  }

  handleFilter = (e, { value }) => {
    this.setState({ tagFilter: value })
  }

  removeFilter = () => {
    this.setState({ tagFilter: "" })
  }

  tagOptions = allTags => {
    const sortTags = allTags.sort((a, b) => {
      if (a.name > b.name) {
        return 1
      }
      if (a.name < b.name) {
        return -1
      }
      return 0
    })
    const tagArr = sortTags.map(tag => {
      return {
        key: tag.name,
        text: tag.name,
        value: tag.name
      }
    })
    return tagArr
  }

  filteredSongsByTag = (allSongs) => {
    const filteredSongsByTag = allSongs.filter(song => {
      return song.song.tags.find(tag => {
        if (this.state.tagFilter !== "") {
          return tag.name === this.state.tagFilter
        } else {
          return tag
        }
      })
    })
    this.mappedFeed(filteredSongsByTag)
  }

  mappedFeed = (filteredSongsByTag) => {
    const mappedFeed = filteredSongsByTag.map(songData => {
      return (
        <div className={`waveform-${songData.id}`}>
          <SongFeedComponent songData={songData} />
        </div>
      )
    })
    return mappedFeed
  }

  render() {
    const { allSongs, followeds, followers, allTags } = this.props
    const tagOptions = this.tagOptions(allTags)


    // return allSongs.length > 0 ? (
    return (
      <div className="feed-container">
        <Grid columns={2} style={{ margin: 20 }}>
          <Grid.Column>{_.reverse(this.filteredSongsByTag())}</Grid.Column>
          <Grid.Column>
            <h3>
              Filter by tag:
              <Dropdown
                header="Filter by Tag"
                onChange={this.handleFilter}
                options={tagOptions}
                search
                value={this.state.tagFilter}
                icon="hashtag"
                iconposition="left"
              />
              {this.state.tagFilter !== "" ? (
                <Button onClick={this.removeFilter}>Remove Filter</Button>
              ) : null}
            </h3>
          </Grid.Column>
        </Grid>
      </div>
    )
    // : (
    //   <div>loading...</div>
    // )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    followeds: state.followeds,
    followers: state.followers,
    // allUsers: state.allUsers,
    allSongs: state.allSongs,
    allTags: state.allTags
  }
}

export default connect(
  mapStateToProps,
  null
)(withRouter(FeedContainer))
