import React, { Component } from "react"
import { connect } from "react-redux"
import { Button, Form, Grid, Header, Message, Segment } from "semantic-ui-react"

class NewSongForm extends Component {
  constructor(props) {
    super()
    this.audioInput = React.createRef()
    this.state = {
      title: "",
      genre: "",
      description: "",
      user_id: "",
      song_link: ""
    }
  }

  handleNewSongSubmit = e => {
    e.preventDefault()
    const { title, genre, description, song_link } = this.state
    // console.log(this.audioInput.current.files[0])
    // const newAudio = this.audioInput.current.files[0]
    // debugger
    let songData = new FormData()
    songData.append("song_link", song_link)
    songData.append("song[title]", title)
    songData.append("song[genre]", genre)
    songData.append("song[description]", description)
    songData.append("song[user_id]", this.props.currentUser.id)
    // const songData = {...this.state, song_link: newAudio, user_id: this.props.currentUser.id}
    const fetchSongsUrl = "http://localhost:3000/songs"
    const reqObj = {
      method: "POST",
      headers: {
        // Accepts: "application/json"
        'Content-Type': "multipart/form-data"
      },
      body: songData
    }
    fetch(fetchSongsUrl, reqObj)
      .then(res => res.json())
      .then(console.log)
  }

  handleNewSongChange = e => {
    this.setState({ [e.target.name]: e.target.value })
  }

  onFilesAdded = e => {
    const targetSongLink = e.target.files[0]
    this.setState({ song_link: targetSongLink })
  }

  render() {
    console.log(this.state)
    console.log(this.props)
    return (
      <div className="new-song-form">
        <Grid textAlign="center" verticalAlign="middle">
          <Grid.Column>
            <Form
              size="large"
              onSubmit={this.handleNewSongSubmit}
              encType="multipart/form-data"
            >
              <Segment stacked>
                <Header as="h1">New Song</Header>
                <Form.Input
                  onChange={this.handleNewSongChange}
                  placeholder="Title"
                  type="text"
                  name="title"
                />
                <Form.Input
                  onChange={this.handleNewSongChange}
                  placeholder="Genre"
                  type="text"
                  name="genre"
                />
                <Form.Input
                  onChange={this.handleNewSongChange}
                  placeholder="Description"
                  type="text"
                  name="description"
                />
                <input
                  type="file"
                  ref={this.audioInput}
                  onChange={this.onFilesAdded}
                />

                <Button type="submit" primary fluid size="large">
                  Submit New Song
                </Button>
              </Segment>
            </Form>
          </Grid.Column>
        </Grid>
      </div>
    )
  }
}

export default connect(
  null,
  null
)(NewSongForm)
