/*eslint-disable */
import React, { Component } from "react"
import { Link, withRouter } from "react-router-dom"
import { connect } from "react-redux"
import {
  Button,
  Grid,
  Header,
  Label,
  Sticky,
  Search,
  Icon
} from "semantic-ui-react"
import { logoutUser, findDisplayUser, isCurrentUser } from "../actions"
import _ from "lodash"
import { async } from "q"

class Nav extends Component {
  state = {
    isLoading: false,
    value: "",
    results: []
  }

  handleLogout = e => {
    localStorage.clear()
    this.props.logoutUser()
    this.props.history.push("/login")
  }

  handleSearchChange = (e, { value }) => {
    this.setState({ value: value })
    this.filterSearchResults()
  }

  filterSearchResults = () => {
    /**
     * @arrayOfAllUsers , @arrayOfAllSongs  @arrayOfEverything
     */
    // * First search for users
    // * Have the current Search Value
    console.log('filtering')
    const { allUsers } = this.props
    const { value } = this.state
    let filteredResults = allUsers.filter(user => {
      return user.username.includes(value)
    })
    console.log(filteredResults)
    this.setState({results: filteredResults})
  }

  goToCurrentUserProfile = user_id => {
    this.props.history.push(`/profile/${user_id}`)
    this.props.findDisplayUser(this.props.allUsers, this.props.history)
    // this.props.isCurrentUser(true)
  }

  render() {
    console.log(this.state)

    const { isLoggedIn } = this.props.user
    const navButtonStyle = { padding: "1% 2% 1% 1%" }
    const eachButtonStyle = { margin: "0% 1%" }
    // console.log(this.props.user)
    // TODO Add Seach Function
    // TODO Add Play/Pause to NavBar

    return (
      <Sticky className='nav-bar-container'>
        {!!isLoggedIn ? (
          <Button.Group style={navButtonStyle} padded='vertically'>
            <Button
              as={Link}
              to='/'
              icon='music'
              label='Feed'
              floated='left'
              style={eachButtonStyle}
            />
            <Button
              as={Link}
              onClick={user_id =>
                this.goToCurrentUserProfile(this.props.user.id)
              }
              style={eachButtonStyle}
              label='Prof'
              icon='user'
            />
            <Button
              as={Link}
              to='/newsong'
              icon='upload'
              style={eachButtonStyle}
              label='Upload Song'
            />
            <Button
              as={Link}
              to='/editaccount'
              icon='edit'
              label='Edit Profile'
            />
            <Button
              style={{ margin: "0% 1%", floated: "right" }}
              as={Link}
              onClick={this.handleLogout}
              icon='log out'
              label='Logout'
            />
          </Button.Group>
        ) : (
          <Button.Group style={eachButtonStyle} padded='vertically'>
            <Button
              as={Link}
              to='/'
              icon='music'
              style={eachButtonStyle}
              label='Feed'
            />
            <Button
              as={Link}
              to='/login'
              icon='sign-in'
              style={eachButtonStyle}
              label='Login'
            />
          </Button.Group>
        )}
        {/* <Button name="play-pause" onClick={null} circular icon="play" /> */}
        {/* <Button name="play-pause" onClick={null} circular icon="pause" /> */}
        <Search
          results={this.state.results}
          onSearchChange={_.debounce(this.handleSearchChange, 500)}
          showNoResults={false}
        />
      </Sticky>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    allUsers: state.allUsers
  }
}

export default connect(mapStateToProps, {
  logoutUser,
  findDisplayUser,
  isCurrentUser
})(withRouter(Nav))
