/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"
import { Button, Grid, Header } from "semantic-ui-react"

import Waveform from "./Waveform"

class SongFeedComponent extends Component {
  render() {
    // ! Need current song with data
    const { songData } = this.props
    const {
      songData: { song }
    } = this.props
    const {
      songData: {
        song: { user }
      }
    } = this.props
    // console.log(this.props)
    return (
      <div className={`song-comp-${song.id}`}>
        <Grid textAlign="left" verticalAlign="left" divided="vertically">
          {/* <Grid.Column> */}
          <Grid.Row>
            {/* <Header to={`/profile/${song.id}`} as="h4">{song.title}</Header> */}
            <p>{song.title}</p>
          </Grid.Row>
          <Grid.Row>
            <Header to={`/profile/${user.id}`} as="h2">
              {user.username}
            </Header>
          </Grid.Row>
          <Grid.Row>
            <Waveform
              song_link={songData.song_link}
              waveHeight={100}
              responsive={true}
              splitChannels={false}
              mediaControls={true}
              maxCanvasWidth={10}
            />
          </Grid.Row>
          {/* </Grid.Column> */}
        </Grid>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
    // allUsers: state.allUsers,
    // allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  // null,
  null
)(withRouter(SongFeedComponent))
