/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"
import { Button, Grid, Header } from "semantic-ui-react"

import Waveform from "./Waveform"

class SongFeedComponent extends Component {
  state = {}

  render() {
    // ! Need current song with data
    const { songData } = this.props
    const { songData: {song} } = this.props
    const { songData: {song: {user}} } = this.props
    // console.log(this.props)
    return (
      <Grid>
        <div className={`song-comp-${song.id}`}>
          <h1 to={`/profile/${song.id}`} as={Link}>{song.title}</h1><br />
          <h1 to={`/profile/${user.id}`} as={Link}>{user.username}</h1><br />
          <h2>{song.genre}</h2>
          <h3>{song.description}</h3>
          <Waveform
            song_link={songData.song_link}
            waveHeight={100}
            responsive={true}
            splitChannels={false}
            mediaControls={false}
            maxCanvasWidth={10}
          />
        </div>
      </Grid>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
    // allUsers: state.allUsers,
    // allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  // null,
  null
)(withRouter(SongFeedComponent))
