/*eslint-disable */
import React, { Component } from "react"
import { Button, Form, Header } from "semantic-ui-react"
import { connect } from "react-redux"
import { updateCurrentUser } from "../actions"
import { withRouter } from "react-router-dom"

class EditUserForm extends Component {
  state = {
    username: "",
    name: "",
    // password: "",
    location: "",
    bio: "",
    img_url: "",
    facebook_url: "",
    twitter_url: "",
    soundclound_url: ""
  }

  componentDidMount() {
    console.log(this.props)
    this.setState(this.props.user)
  }

  handleCreateNewUser = e => {
    e.preventDefault()
    this.props.updateCurrentUser(
      this.props.user,
      this.state,
      this.props.history
    )
  }
  componentDidUpdate(prevProps) {
    // Typical usage (don't forget to compare props):
    if (this.props.user.id !== prevProps.user.id) {
      this.setState(this.props.user)
    }
  }

  handleEditUserChange = e => {
    const targetValue = e.target.value
    const targetName = e.target.name
    this.setState({ [targetName]: targetValue })
  }

  render() {
    if (!this.props.user.id) {
      return (
        <div>Loading...</div>
      )
    }
    const {
      username,
      name,
      // password,
      location,
      bio,
      img_url,
      facebook_url,
      soundclound_url,
      twitter_url
    } = this.props.user
    return (
      <div className="edit-user-container">
        <Form className="edit-user-form" onSubmit={this.handleCreateNewUser}>
          <Form.Field>
            <Header as="h1">Edit Profile</Header>
            <Form.Input
              label='Name: '
              onChange={this.handleEditUserChange}
              value={name}
              placeholder="Name"
              type="text"
              name="name"
            />
            <Form.Input
              onChange={this.handleEditUserChange}
              label='Username: '
              value={username}
              placeholder="Username"
              type="text"
              name="username"
            />

            {/* <Form.Input
              onChange={this.handleEditUserChange}
              value={password}
              placeholder="Password"
              type="password"
              name="password"
            /> */}
            <Form.Input
              onChange={this.handleEditUserChange}
              label='Location: '
              value={location}
              placeholder="Location"
              type="text"
              name="location"
            />
            <Form.Input
              onChange={this.handleEditUserChange}
              label='Bio: '
              value={bio}
              placeholder="Bio"
              type="textArea"
              name="bio"
            />
          </Form.Field>
          {/* TODO Add Socials & Images */}
          <Form.Field>
            <Form.Input
              onChange={this.handleEditUserChange}
              placeholder="Image Url"
              label="Image Url: "
              type="text"
              name="img_url"
            />
            <Form.Input
              onChange={this.handleEditUserChange}
              placeholder="Facebook"
              label="Facebook: "
              type="text"
              name="facebook_url"
            />
            <Form.Input
              onChange={this.handleEditUserChange}
              placeholder="Twitter"
              label="Twitter: "
              type="text"
              name="twitter_url"
            />
            <Form.Input
              onChange={this.handleEditUserChange}
              placeholder="SoundCloud"
              label="SoundCloud: "
              type="text"
              name="soundcloud_url"
            />
          </Form.Field>
          <Button type="submit" primary size="medium">
            Edit Profile
          </Button>
        </Form>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  }
}

export default connect(
  mapStateToProps,
  { updateCurrentUser }
)(withRouter(EditUserForm))
