import React, { Component } from 'react';
import { connect } from "react-redux"
import { withRouter } from 'react-router-dom'
import Waveform from './Waveform';
// import { fetchAllSongs } from "../actions"

class FeedContainer extends Component {

  // componentDidMount() {
  //   this.props.fetchAllSongs()
  // }
  render() { 
    // const {} = this.props
    // console.log('feed props: ', this.props)
    let songComp
    if (this.props.allSongs[9]) {
      songComp = this.props.allSongs[9]
      console.log('song playing: ', songComp.song_link)
    }
    return ( 
      <div className='feed-container'>
        da Feed
        {/* Map song components */}
        <audio controls='controls' src={songComp} />
        {/* <Waveform
          src={songComp}
        /> */}
      </div>
     );
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    // allUsers: state.allUsers,
    allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  null
)(withRouter(FeedContainer))
