import React, { Component } from 'react';
import { connect } from "react-redux"
import { withRouter } from 'react-router-dom'
import Waveform from './Waveform';
// import { fetchAllSongs } from "../actions"

class FeedContainer extends Component {
  state = {
    audio: ''
  }
  componentDidMount() {
    this.fetchAudio(45)
  }

  fetchAudio = (song_id) => {
    const fetchURL = 'http://localhost:3000/songs'
    fetch(`${fetchURL}/${song_id}`)
    .then(res=>res.json())
    .then(audio => {
      this.setState({audio: audio.song_audio})
    })
    .catch(err => console.log(err))
  }

  render() { 
    // const {} = this.props
    console.log('feed state: ', this.state)
    let songComp
    // if (this.props.allSongs[9]) {
    //   songComp = this.props.allSongs[9]
    //   console.log('song playing: ', songComp.song_link)
    // }

    const mario = require('../mario.mp3')

    return ( 
      <div className='feed-container'>
        da Feed
        {/* Map song components */}
        <audio controls='controls' src={songComp} />
        <audio controls='controls' src={mario} />
        {/* <Waveform
          src={songComp}
        /> */}
      </div>
     );
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    // allUsers: state.allUsers,
    allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  null
)(withRouter(FeedContainer))
