/*eslint-disable */
import React, { Component } from "react"
import { Button, Form, Header } from "semantic-ui-react"
import { connect } from "react-redux"
import { updateCurrentUser } from "../actions"
import { withRouter } from "react-router-dom"

class EditUserForm extends Component {
  state = {
    username: "",
    name: "",
    // password: "",
    location: "",
    bio: "",
    img_url: "",
    facebook_url: "",
    twitter_url: "",
    soundclound_url: ""
  }

  componentDidMount() {
    console.log(this.props)
    this.setState(this.props.user)
  }
  handleCreateNewUser = e => {
    e.preventDefault()
    this.props.updateCurrentUser(
      this.props.user,
      this.state,
      this.props.history
    )
  }

  handleNewUserChange = e => {
    const targetValue = e.target.value
    const targetName = e.target.name
    this.setState({ [targetName]: targetValue })
  }

  render() {
    console.log(this.state)
    const {
      username,
      name,
      // password,
      location,
      bio,
      img_url,
      facebook_url,
      soundclound_url,
      twitter_url
    } = this.props.user
    return (
      <div className="edit-user-container">
        <Form className="edit-user-form" onSubmit={this.handleCreateNewUser}>
          <Form.Field>
            <Header as="h1">Edit Profile</Header>
            <Form.Input
              onChange={this.handleNewUserChange}
              value={name}
              placeholder="Name"
              type="text"
              name="name"
            />
            <Form.Input
              onChange={this.handleNewUserChange}
              value={username}
              placeholder="Username"
              type="text"
              name="username"
            />

            {/* <Form.Input
              onChange={this.handleNewUserChange}
              value={password}
              placeholder="Password"
              type="password"
              name="password"
            /> */}
            <Form.Input
              onChange={this.handleNewUserChange}
              value={location}
              placeholder="Location"
              type="text"
              name="location"
            />
            <Form.Input
              onChange={this.handleNewUserChange}
              value={bio}
              placeholder="Bio"
              type="textArea"
              name="bio"
            />
          </Form.Field>
          {/* TODO Add Socials & Images */}
          <Form.Field>
            <Form.Input
              onChange={this.handleNewUserChange}
              placeholder="Image Url"
              type="text"
              name="img_url"
            />
            <Form.Input
              onChange={this.handleNewUserChange}
              placeholder="Facebook"
              type="text"
              name="facebook_url"
            />
            <Form.Input
              onChange={this.handleNewUserChange}
              placeholder="Twitter"
              type="text"
              name="twitter_url"
            />
            <Form.Input
              onChange={this.handleNewUserChange}
              placeholder="SoundCloud"
              type="text"
              name="soundcloud_url"
            />
          </Form.Field>
          <Button type="submit" primary fluid size="large">
            SignUp!
          </Button>
        </Form>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  }
}

export default connect(
  mapStateToProps,
  { updateCurrentUser }
)(withRouter(EditUserForm))
