import React, { Component } from "react"
import { connect } from "react-redux"
import { Button, Form, Grid, Header, Message, Segment } from "semantic-ui-react"

class NewSongForm extends Component {
  constructor() {
    super()
    this.state = {
      title: "",
      genre: "",
      description: ""
      // song_link: ''
    }
    this.audioInput = React.createRef()
  }

  handleNewSongSubmit = e => {

  }

  handleNewSongChange = e => {
    this.setState({ [e.target.name]: e.target.value })
  }
  render() {
    // console.log(this.state)
    return (
      <div className="new-song-form">
        <Grid textAlign="center" verticalAlign="middle">
          <Grid.Column>
            <Form size="large" onSubmit={null}>
              <Segment stacked>
                <Header as="h1">New Song</Header>
                <Form.Input
                  onChange={this.handleNewSongChange}
                  fluid
                  placeholder="Title"
                  type="text"
                  name="title"
                />
                <Form.Input
                  onChange={this.handleNewSongChange}
                  fluid
                  placeholder="Genre"
                  type="text"
                  name="genre"
                />
                <Form.Input
                  onChange={this.handleNewSongChange}
                  fluid
                  placeholder="Description"
                  type="text"
                  name="description"
                />
                <Form.Input
                  onChange={this.handleNewSongChange}
                  fluid
                  placeholder="Audio File"
                  type="file"
                  ref={this.audioInput}
                  name="song_link"
                />

                <Button type="submit" primary fluid size="large">
                  Submit New Song
                </Button>
              </Segment>
            </Form>
          </Grid.Column>
        </Grid>
      </div>
    )
  }
}

export default NewSongForm
