/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { Link, withRouter } from "react-router-dom"

class UserProfile extends Component {

  findUser = (displayUserID) => {
    if (this.props.allUsers.length > 0 ) {
      return this.props.allUsers.find(u => {
        return u.id === Number(displayUserID)
      })
    }
  }
  render() {
    const displayUserID = this.props.history.location.pathname.slice(9)
    // const { username, name, location, bio, id } = this.props.user
    // const { allUsers } = this.props
    // *  Does the this.props.user.id match /profile/${id}?
    const displayUser = this.findUser(displayUserID)
    if (displayUser) {
      const { username, name, location, bio, id } = displayUser
    }
    console.log(displayUser)
    return displayUser ? (
      <div className={`user-profile-${id}`}>
        Profile Page
        <h1>Username: {username}</h1>
        <h1>Name: {name}</h1>
        <h1>location: {location}</h1>
        <p>Bio: {bio}</p>
        <Link onClick={this.props.handleLogout}>Logout</Link>
      </div>
    ) : (
      <div>
        No User Profile
        <Link onClick={this.props.handleLogout}>Logout</Link>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    allUsers: state.allUsers
  }
}
export default connect(
  mapStateToProps,
  null
)(withRouter(UserProfile))
