/*eslint-disable */
import React, { Component } from "react"
import { connect } from "react-redux"
import { withRouter } from "react-router-dom"
import Waveform from './Waveform'

class SongFeedComponent extends Component {
  state = {

  }

  render() {
    // ! Need current song with data
    const { songData } = this.props
    // console.log(this.props)
    return (
      <div className={`song-comp-${songData.id}`}>
        <h1>{songData.song.title}</h1>
        <h1>{songData.song.user.username}</h1>
        <h2>{songData.song.genre}</h2>
        <h3>{songData.song.description}</h3>
        <Waveform 
          song_link={songData.song_link} 
          waveHeight={100}
          responsive={true}
          splitChannels={false}
          mediaControls={false}
          maxCanvasWidth={10}
        />
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    // allUsers: state.allUsers,
    // allSongs: state.allSongs
  }
}

export default connect(
  mapStateToProps,
  // null,
  null
)(withRouter(SongFeedComponent))
